package com.dicoding.applokasicuaca.utilsimport java.text.SimpleDateFormatimport java.util.Calendarimport java.util.Localeclass TimeUtils {    private val timeFormat = SimpleDateFormat("HH:mm:ss", Locale.getDefault())    private val dateFormat = SimpleDateFormat("dd MMMM yyyy", Locale.getDefault())    fun getCurrentTime(): String = timeFormat.format(Calendar.getInstance().time)    fun getCurrentDate(): String = dateFormat.format(Calendar.getInstance().time)    fun getTimeBasedMessage(): String {        return when (Calendar.getInstance().get(Calendar.HOUR_OF_DAY)) {            in 6..10 -> "Waktunya sarapan!"            in 11..14 -> "Waktunya makan siang!"            in 18..20 -> "Waktunya makan malam!"            else -> "Bukan waktu makan saat ini."        }    }    companion object {        fun getTimeBasedIconUrl(): String {            return when (Calendar.getInstance().get(Calendar.HOUR_OF_DAY)) {                in 6..10 -> "https://github.com/riyanfazrirahman/assets-recomendation/blob/main/img-recomendation/download%20(5).png?raw=true"                in 11..14 -> "https://github.com/riyanfazrirahman/assets-recomendation/blob/main/img-recomendation/download%20(4).png?raw=true"                in 18..20 -> "https://github.com/riyanfazrirahman/assets-recomendation/blob/main/img-recomendation/download%20(6).png?raw=true"                else -> "https://github.com/riyanfazrirahman/assets-recomendation/blob/main/img-recomendation/anime-programmer-working-code_1282444-263455.png?raw=true"            }        }    }}